function [data, freqs] = get_data_bychan(chans,infname)infid = [];if nargin < 1	error('What''s the channel')	endif nargin < 2	[infid, infname] = get_fid('rb');else	infid = fopen(infname,'rb');endif isempty(infname)	[infid, infname] = get_fid('rb');endversion = fread(infid,1,'int16');if version ~= -3 &  length(chans) == 1	error('specify a channel pair, e.g. [60 73]')end	[nfiles,obs_labels,Epoch,Window_Length,NEpoch,Nbad_chan,bad_chan,ref_flag,reference,NChan,NFreq] = rd_anal_hdr(infid);data = zeros(nfiles,NFreq(1));for i = 1:nfiles	obs_trial = fread(infid,[NChan(i),NFreq(i)],'float');	obs_trial = obs_trial';	if version == -3		data(i,:) = obs_trial(:,chans)';		else		ch_pair_indices;		data(i,:) = obs_trial(:,chpair(chans(1),chans(2)))'; 	endend;freqs = [0:size(data,2)-1]/Epoch(1);fclose('all');	