function [xelec,yelec,zelec] = electrodes(NChan)%modified to take 65 chan data. 12/13/98 Joseph Dienglobal code_pathglobal position_fileif NChan == 129position_file = ['''' code_path  'oregon129.xyz'''];eval(['load ' position_file ' -ascii']);oregon = oregon129';xelec = oregon(2,:);yelec = oregon(3,:);zelec = oregon(4,:);end;if NChan == 65position_file = ['''' code_path  'oregon65.xyz'''];eval(['load ' position_file ' -ascii']);oregon = oregon65';xelec = oregon(2,:);yelec = oregon(3,:);zelec = oregon(4,:);end;